import React, { useEffect, useState } from 'react';
import ProjectImage from '../projectImage/ProjectImage';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Gallery from '../gallery/Gallery';
import style from './GalleryContainer.module.css';

const GalleryContainer = (props) => {
	const params = useParams().gallery;

	useEffect(() => {
		axios
			.get(
				`http://localhost:8000/wp-json/custom-api/v1/gallery_images?type=${type}&slug=${slug}&gallery_slug=${params.gallerySlug}`
			)
			.then((res) => {
				console.log(res.data);
				setContent(res.data);
			})
			.then(() => setLoaded(true));
	}, []);

	const [galleryView, toggleGalleryView] = useState(false);

	const [imageRef, setImageRef] = useState(null);

	console.log(props);

	const raiseClick = (id) => {
		setImageRef(id);
		toggleGalleryView(true);
	};

	if (!galleryView) {
		return (
			<div className={style.container}>
				<h1></h1>
				<div className={style.imagesContainer}>
					{/* {images.map((image) => (
						<ProjectImage
							onClick={() => raiseClick(image.id)}
							image={image}
							key={image.id}
						/>
					))} */}
				</div>
			</div>
		);
	}

	if (galleryView) {
		return (
			// <Gallery
			// 	onClick={() => toggleGalleryView(false)}
			// 	images={images}
			// 	imageRef={imageRef}
			// />

			<div></div>
		);
	}
};

export default GalleryContainer;
