import React, { Component, useState, useEffect } from 'react';
import axios from 'axios';
import Image from '../image/Image';
import style from './Project.module.css';
import { CSSTransitionGroup } from 'react-transition-group';
import { motion } from 'framer-motion';
import {
	BrowserRouter as Router,
	Routes,
	Route,
	Link,
	Outlet,
	useParams,
	useLocation,
	usePrompt,
} from 'react-router-dom';

const Project = (props) => {
	const [projectDescription, setProjectDescription] = useState('');
	const [projectTitle, setProjectTitle] = useState('');
	const [images, setImages] = useState([]);
	const [thumbnail, setThumbnail] = useState('');

	const params = useParams();

	useEffect(() => {
		axios.get(`/wp-json/wp/v2/project?slug=${params.slug}`).then(
			(res) => (
				console.log(res.data[0]),
				setProjectDescription(res.data[0].acf.description),
				setProjectDescription(res.data[0].acf.title),
				setThumbnail(res.data[0].acf.thumbnail.sizes.medium)
			)
			// setProjectTitle(res.data[0].title.rendered),
			// setImages(res.data[0].acf.images)
		);
	}, [params]);

	return (
		<div>hi</div>
		// <div className={style.projectContainer}>
		// 	<div>{projectTitle}</div>
		// 	<div dangerouslySetInnerHTML={{ __html: projectDescription }} />
		// 	{images.map((image) => (
		// 		<Image key={image.id} image={image} />
		// 	))}
		// </div>
	);
};

export default Project;
