import React, { Component, useState, useEffect } from 'react';
import axios from 'axios';
import Image from '../image/Image';
import ProjectGallery from '../projectGallery/ProjectGallery';
import style from './Project.module.css';
import { CSSTransitionGroup } from 'react-transition-group';
import { motion } from 'framer-motion';
import {
	BrowserRouter as Router,
	Routes,
	Route,
	Link,
	Outlet,
	useParams,
	useLocation,
	usePrompt,
} from 'react-router-dom';

const Project = (props) => {
	const [projectDescription, setProjectDescription] = useState('');
	const [projectTitle, setProjectTitle] = useState('');
	const [images, setImages] = useState({});
	const [galleries, setGalleries] = useState({});
	const [loaded, setLoaded] = useState(false);

	const params = useParams();

	useEffect(() => {
		axios.get(`/wp-json/wp/v2/project?slug=${params.slug}`).then(
			(res) =>
				(console.log(res.data[0]),
				setProjectDescription(res.data[0].acf.description),
				setProjectDescription(res.data[0].acf.title),
				setGalleries(
					res.data[0].acf.project_galleries[0].project_gallery
				)).then(setLoaded(true))
			// setProjectTitle(res.data[0].title.rendered),
			// setImages(res.data[0].acf.images)
		);
	}, [params]);

	return (
		!loaded &&
		(<div>loading</div>)(loaded && <ProjectGallery galleries={galleries} />)

		// <div className={style.projectContainer}>
		// 	<div>{projectTitle}</div>
		// 	<div dangerouslySetInnerHTML={{ __html: projectDescription }} />
		// 	{images.map((image) => (
		// 		<Image key={image.id} image={image} />
		// 	))}
		// </div>
	);
};

export default Project;
