import React, { Component } from "react";
import axios from "axios";
import Image from "../image/Image";

export default class Medium extends Component {
  state = {
    work: {},
    isLoaded: false,
    slug: null,
  };

  componentDidMount() {
    if (!this.state.slug == this.props.match.params.slug) {
      axios
        .get(`/wp-json/wp/v2/work?slug=${this.props.match.params.slug}`)
        .then((res) =>
          this.setState({
            work: res.data,
            isLoaded: true,
            slug: this.props.match.params.slug,
          })
        )
        .catch((err) => console.log(err));
    }
  }

  render() {
    const { work, isLoaded } = this.state;
    if (isLoaded) {
      const images = work[0].acf.images;

      return (
        <div>
          {images.map((image) => (
            <Image key={image.id} image={image} />
          ))}
        </div>
      );
    }

    return null;
  }
}
