import React, { useState } from 'react';
import Link from 'react-router-dom';
import ProjectGalleryThumbnail from '../projectGalleryThumbnail/ProjectGalleryThumbnail';
import ProjectGallery from '../projectGallery/ProjectGallery';

const ProjectGalleryContainer = (props) => {
	const galleries = props.galleries;
	console.log(galleries);
	const [currentGallery, setCurrentGallery] = useState({});
	const [galleryView, toggleGalleryView] = useState(false);

	if (!galleryView) {
		return (
			<>
				{galleries.map((gallery) => (
					<ProjectGalleryThumbnail gallery={gallery} />
				))}
			</>
		);
	}
};

export default ProjectGalleryContainer;
