import React, { Component, useState, useEffect } from 'react';
import axios from 'axios';
import Image from '../image/Image';
import { AnimatePresence, motion } from 'framer-motion';
import { useParams } from 'react-router-dom';
import Gallery from '../gallery/Gallery';

const Medium = () => {
	const { slug } = useParams();
	const [loaded, setLoaded] = useState(false);
	const [medium, setMedium] = useState({});
	const [images, setImages] = useState([]);
	const [gallery, setGallery] = useState(false);

	//animations
	const containerVariant = {
		initial: {
			opacity: 0,
		},
		animate: {
			opacity: 1,
			transition: { delay: 0.2, duration: 0.2 },
		},

		exit: {
			opacity: 0,
			transition: {
				duration: 0.2,
			},
		},
	};

	useEffect(() => {
		axios
			.get(`/wp-json/wp/v2/work?slug=${slug}`)
			.then((res) => setImages(res.data[0].acf.images));
	}, [slug]);

	const handleGallery = () => {
		gallery ? setGallery(false) : setGallery(true);
	};

	if (!gallery) {
		return (
			<AnimatePresence exitBeforeEnter>
				<motion.div
					key={slug}
					variants={containerVariant}
					initial="initial"
					animate="animate"
					exit="exit"
				>
					{images.map((image) => (
						<Image key={image.id} image={image} onClick={handleGallery} />
					))}
				</motion.div>

				{gallery && <Gallery onClick={handleGallery} />}
			</AnimatePresence>
		);
	}
};

export default Medium;
