import React, { Component, useState, useEffect } from 'react';
import axios from 'axios';
import Image from '../image/Image';

import style from './Project.module.css';
import { CSSTransitionGroup } from 'react-transition-group';
import { motion } from 'framer-motion';
import {
	BrowserRouter,
	Routes,
	Route,
	NavLink,
	Outlet,
	useParams,
	useLocation,
	usePrompt,
	useHistory,
} from 'react-router-dom';
import { useRouteMatch } from 'react-router';
import ProjectGalleryThumbnail from '../projectGalleryThumbnail/ProjectGalleryThumbnail';
import ProjectGallery from '../projectGallery/ProjectGallery';

const Project = (props) => {
	const [projectDescription, setProjectDescription] = useState('');
	const [projectTitle, setProjectTitle] = useState('');
	const [projectSlug, setProjectSlug] = useState('');
	const [loaded, setLoaded] = useState(false);
	const [projectGalleries, setProjectGalleries] = useState([]);

	const params = useParams();

	if (params.subProject) {
		console.log('subProject');
	} else {
		console.log('project');
	}

	// let projectsEndpoint = `http://localhost:8000/wp-json/custom-api/v1/projects?slug=${params.slug}`;
	// let projectGalleriesEndpoint = `http://localhost:8000/wp-json/custom-api/v1/project_galleries?slug=${params.slug}`;

	const projectsRequest = axios.get(() => {
		if (params.subProject) {
			return String(
				`http://localhost:8000/wp-json/custom-api/v1/sub_projects?slug=${params.slug}`
			);
		} else {
			return String(
				`http://localhost:8000/wp-json/custom-api/v1/projects?slug=${params.slug}`
			);
		}
	});
	const projectGalleriesRequest = axios.get((params) => {
		if (params.subProject) {
			return String(
				`http://localhost:8000/wp-json/custom-api/v1/sub_project_galleries?slug=${params.subProject}`
			);
		} else {
			return String(
				`http://localhost:8000/wp-json/custom-api/v1/project_galleries?slug=${params.slug}`
			);
		}
	});

	useEffect(() => {
		axios
			.all([projectsRequest, projectGalleriesRequest])
			.then(
				axios.spread((...responses) => {
					const projectsResponse = responses[0].data[0];
					const projectGalleriesResponse = responses[1].data;

					setProjectTitle(projectsResponse.title);
					setProjectDescription(projectsResponse.description);

					setProjectGalleries(projectGalleriesResponse);
				})
			)

			.then(() => setLoaded(true));
	}, [params]);

	if (!loaded) {
		return (
			<div>
				loading
				<Outlet />
			</div>
		);
	}

	if (loaded) {
		// return <ProjectGalleryContainer galleries={galleries} />;

		return (
			<div>
				<h1>{projectTitle}</h1>
				<h2>{projectDescription}</h2>
				{projectGalleries.map((gallery) => (
					<NavLink to={`/gallery/${gallery.gallery_slug}`}>
						<ProjectGalleryThumbnail gallery={gallery} />
					</NavLink>
				))}
				<Outlet />
			</div>
		);
	}

	// <div className={style.projectContainer}>
	// 	<div>{projectTitle}</div>
	// 	<div dangerouslySetInnerHTML={{ __html: projectDescription }} />
	// 	{images.map((image) => (
	// 		<Image key={image.id} image={image} />
	// 	))}
	// </div>
};

export default Project;
