import React, { Component, useState, useEffect } from 'react';
import axios from 'axios';
import Image from '../image/Image';
import { useQuery, gql } from '@apollo/client';
import { motion } from 'framer-motion';

const Medium = (props) => {
	const slug = String(props.match.params.slug);
	const GET_WORKS = gql`
		query GET_WORKS($slug: String) {
			work(id: $slug, idType: SLUG) {
				id
				content(format: RENDERED)
				slug
				title(format: RENDERED)
				worksMeta {
					images {
						caption(format: RENDERED)
						description(format: RENDERED)
						sourceUrl(size: LARGE)
						title(format: RENDERED)
						id
					}
				}
			}
		}
	`;

	const { loading, error, data } = useQuery(GET_WORKS, {
		variables: { slug: slug },
	});

	// useEffect(() => {
	//   axios
	//     .get(`/wp-json/wp/v2/work?slug=${slug}`)
	//     .then((res) => setImages(res.data[0].acf.images))
	//     .then(setLoaded(true));

	//   axios
	//     .get(`/wp-json/wp/v2/work?slug=${slug}`)
	//     .then((res) => setMedium(res.data[0]))

	//     .catch((err) => console.log(err));
	// }, [slug]);
	if (loading) {
		return (
			<motion.div
				initial={{ opacity: 0 }}
				animate={{ opacity: 1 }}
				transition={{ delay: 5 }}
			>
				loading
			</motion.div>
		);
	}

	return (
		<motion.div
			initial={{ opacity: 0 }}
			animate={{ opacity: 1 }}
			transition={{ delay: 5 }}
		>
			{console.log(data)}
			{data.work.worksMeta.images.map((image) => (
				<Image key={image.id} image={image} />
			))}
			<div>hi</div>
		</motion.div>
	);
};

export default Medium;
