    query GetProject {
      projects {
        edges {
          node {
            slug
            title
            content
            projectMeta {
              year
              images {
                sourceUrl
                caption
                description
              }
            }
          }
        }
      }
    }
  `;

  const { loading, error, data } = useQuery(GET_PROJECT);
  console.log(data);

