import React, { Component, useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import style from './Nav.module.css';
import ProjectSubNav from '../projectSubNav/ProjectSubNav';
import axios from 'axios';

const Nav = (props) => {
	const [hidden, sethidden] = useState(true);
	const [mediumHidden, setMediumHidden] = useState(true);
	const [projectHidden, setProjectHidden] = useState(true);
	const [loaded, setLoaded] = useState(false);
	const [projects, setProjects] = useState({});

	useEffect(() => {
		axios
			.get(`/wp-json/wp/v2/project`)
			.then((res) => {
				setProjects(res.data);
			})
			.then(() => setLoaded(true));
	}, []);

	// const navProjects = data;

	const raiseClick = () => {
		setProjectHidden(true);
	};

	return (
		<nav className={style.sideNav}>
			<div className={style.logo}> logo</div>

			<ul className={style.navList}>
				<NavLink to="/">
					<li>Home</li>
				</NavLink>
				<li
					onClick={
						projectHidden
							? () => setProjectHidden(false)
							: () => setProjectHidden(true)
					}
				>
					Projects
				</li>
				{!projectHidden && (
					<ProjectSubNav projects={projects} onClick={raiseClick} />
				)}
				<li
					onClick={
						mediumHidden
							? () => setMediumHidden(false)
							: () => setMediumHidden(true)
					}
				>
					Works
				</li>
				{!mediumHidden && (
					<div className={style.infoSubNav}>
						<NavLink to="/exhibitions">
							<li>Exhibitions</li>
						</NavLink>
						<NavLink to="/sculpture">
							<li>Sculpture</li>
						</NavLink>
						<NavLink to="/digital-installations">
							<li>Digital Installations</li>
						</NavLink>
						<NavLink to="/paintings">
							<li>Painting</li>
						</NavLink>
						<NavLink to="/photography">
							<li>Photography</li>
						</NavLink>
						<NavLink to="/print">
							<li>Print</li>
						</NavLink>
					</div>
				)}
				<li onClick={hidden ? () => sethidden(false) : () => sethidden(true)}>
					Info
				</li>
				{!hidden && (
					<div className={style.infoSubNav}>
						<NavLink to="/">
							<li>Resume CV</li>
						</NavLink>
						<NavLink to="/">
							<li>Bio</li>
						</NavLink>
						<NavLink to="/">
							<li>Statement</li>
						</NavLink>
						<NavLink to="/">
							<li>News</li>
						</NavLink>
						<NavLink to="/">
							<li>Architecture</li>
						</NavLink>
					</div>
				)}
			</ul>
		</nav>
	);
};

export default Nav;
