import React, { useEffect, useState } from 'react';
import ProjectImage from '../projectImage/ProjectImage';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Gallery from '../gallery/Gallery';

const ProjectGallery = () => {
	const params = useParams().gallery;
	console.log(params);
	const [images, setimages] = useState([]);
	const [galleryView, toggleGalleryView] = useState(false);
	const [imageRef, setImageRef] = useState('');

	useEffect(() => {
		axios.get(`/wp-json/wp/v2/project_gallery?slug=${params}`).then((res) => {
			console.log(res.data[0]);
			setimages(res.data[0].acf.images);
		});
	}, [params]);

	const raiseClick = (id) => {
		console.log(id);
		setImageRef(id);
		toggleGalleryView(true);
	};

	if (!galleryView) {
		return (
			<>
				{images.map((image) => (
					<ProjectImage
						onClick={() => raiseClick(image.id)}
						image={image}
						key={image.id}
					/>
				))}
			</>
		);
	}

	if (galleryView) {
		return <Gallery images={images} />;
	}
};

export default ProjectGallery;
