import React, { Component, useState, useEffect } from 'react';
import axios from 'axios';
import Image from '../image/Image';
import ProjectGalleryContainer from '../projectGalleryContainer/ProjectGalleryContainer';
import style from './Project.module.css';
import { CSSTransitionGroup } from 'react-transition-group';
import { motion } from 'framer-motion';
import {
	BrowserRouter as Router,
	Routes,
	Route,
	NavLink,
	Outlet,
	useParams,
	useLocation,
	usePrompt,
	useRouteMatch,
} from 'react-router-dom';
import ProjectGalleryThumbnail from '../projectGalleryThumbnail/ProjectGalleryThumbnail';

const Project = (props, { match }) => {
	const [projectDescription, setProjectDescription] = useState('');
	const [projectTitle, setProjectTitle] = useState('');
	const [projectSlug, setProjectSlug] = useState('');
	const [loaded, setLoaded] = useState(false);
	const [projectGalleries, setProjectGalleries] = useState([]);

	const params = useParams();

	let { path, url } = useRouteMatch();

	useEffect(() => {
		axios
			.get(`/wp-json/wp/v2/project?slug=${params.slug}`)

			.then((res) => {
				console.log(res.data[0]);
				setProjectTitle(res.data[0].acf.title);
				setProjectDescription(res.data[0].acf.description);
				setProjectSlug(res.data[0].slug);
				console.log(res.data[0].slug);
				return axios.get(
					`/wp-json/wp/v2/gallery_project?slug=${res.data[0].slug}`
				);
			})
			.then((res) => {
				return axios.get(
					`/wp-json/wp/v2/project_gallery?gallery_project=${res.data[0].id}`
				);

				// res.data.map(
				// 	(projectGallery) =>
				// 		projectGallery._embedded['wp:term'][0][0].slug == params.slug &&
				// 		projectGalleries.push(projectGallery)
				// );
				// console.log(projectGalleries);
			})
			.then((res) => {
				console.log(res.data);
				setProjectGalleries(res.data);
			})

			.then(() => setLoaded(true));
	}, [params]);

	if (!loaded) {
		return <div>loading</div>;
	}

	if (loaded) {
		// return <ProjectGalleryContainer galleries={galleries} />;

		return projectGalleries.map((gallery) => (
			<NavLink to={`${url}/${gallery.slug}`}>
				<ProjectGalleryThumbnail gallery={gallery} />
			</NavLink>
		));
	}

	// <div className={style.projectContainer}>
	// 	<div>{projectTitle}</div>
	// 	<div dangerouslySetInnerHTML={{ __html: projectDescription }} />
	// 	{images.map((image) => (
	// 		<Image key={image.id} image={image} />
	// 	))}
	// </div>
};

export default Project;
