import React, { Component } from 'react';

import { motion } from 'framer-motion';

const containerStyle = {
	position: 'relative',
	width: '3rem',
	height: '3rem',
	boxSizing: 'border-box',
	display: 'flex',

	// minHeight: '100vh',
	// minWidth: '100vw',
};

const circleStyle = {
	display: 'block',
	width: '3rem',
	height: '3rem',
	border: '0.5rem solid #e9e9e9',
	borderTop: '0.5rem solid #3498db',
	borderRadius: '50%',
	position: 'absolute',
	boxSizing: 'border-box',

	top: '50vw',
	left: '50vh',
};

const spinTransition = {
	loop: Infinity,
	ease: 'linear',
	duration: 1,
};

const Loading = () => {
	return (
		<div style={containerStyle}>
			<motion.span
				style={circleStyle}
				animate={{ rotate: 360 }}
				transition={spinTransition}
			/>
		</div>
	);
};

export default Loading;
