import React from 'react';
import style from './MainHeader.module.css';

const MainHeader = (props) => {
	return (
		<h1 className={style.container}>
			{props.content.split(' ').map((word) => {
				if (word.length == 1) {
					return <span className={style.lower}>{`${word} `}</span>;
				} else return <span className={style.upper}>{word}</span>;
			})}
		</h1>
	);
};

export default MainHeader;
