import React, { Component, useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import style from './Nav.module.css';
import SubNav from '../subNav/SubNav';
import axios from 'axios';

const Nav = (props) => {
	const [subNavActive, setSubNavActive] = useState(false);
	const [loaded, setLoaded] = useState(false);
	const [projects, setProjects] = useState({});
	const [works, setWorks] = useState({});

	useEffect(() => {
		axios
			.get(`http://localhost:8000/wp-json/custom-api/v1/get_nav_items`)
			.then((res) => {
				console.log(res.data);
				setProjects(res.data[0].projects);
				setWorks(res.data[1].works);
			})
			.then(() => setLoaded(true));
	}, []);

	const subNav = (type) => {
		setSubNavActive(true);

		return (
			<SubNav
				type={type}
				// onClick={() => raiseClick}
				// subProjectOpen={subProjectOpen}
				// subProjects={subProjects}
			/>
		);
	};

	return (
		<nav className={style.sideNav}>
			<ul className={style.navList}>
				<NavLink to="/projects">
					<li onClick={subNav(project)}>Projects</li>
				</NavLink>
				<NavLink>
					<li onClick={null}>Works</li>
				</NavLink>
			</ul>

			{subNavActive && { subNav }}
		</nav>
	);
};

export default Nav;
