import React, { Component } from "react";
import axios from "axios";
import Image from "../image/Image";

export default class Medium extends Component {
  state = {
    work: {},
    isLoaded: false,
    images: [],
  };

  static getDerivedStateFromProps() {
    axios
      .get(`/wp-json/wp/v2/work?slug=${this.props.match.params.slug}`)
      .then((res) =>
        this.setState({
          work: res.data[0],
          isLoaded: true,
        })
      )
      .catch((err) => console.log(err));
  }

  render() {
    const { work, isLoaded } = this.state;
    if (isLoaded) {
      const images = work.acf.images;

      return (
        <div>
          {images.map((image) => (
            <Image key={image.id} image={image} />
          ))}
        </div>
      );
    }

    return null;
  }
}
